const r=()=>{const t=document.querySelector(".marquee-top-integrations"),a=document.querySelector(".marquee-bot-integrations");t?.addEventListener("mouseover",()=>{t.querySelectorAll(".animate-marquee-integrations").forEach(e=>{e.classList.add("pause")})}),a?.addEventListener("mouseover",()=>{a.querySelectorAll(".animate-marquee-integrations").forEach(e=>{e.classList.add("pause")})}),t?.addEventListener("mouseout",()=>{t.querySelectorAll(".animate-marquee-integrations").forEach(e=>{e.classList.remove("pause")})}),a?.addEventListener("mouseout",()=>{a.querySelectorAll(".animate-marquee-integrations").forEach(e=>{e.classList.remove("pause")})})};r();document.addEventListener("astro:after-swap",r);
