class c{switchButton;constructor(t){this.switchButton=t,this.setupEventListeners()}setupEventListeners(){this.switchButton.addEventListener("click",()=>this.handleStateChange()),this.switchButton.addEventListener("keydown",t=>this.handleKeyDown(t))}handleStateChange(){if(this.switchButton.disabled)return;const s=!(this.switchButton.getAttribute("aria-checked")==="true");this.switchButton.setAttribute("aria-checked",s.toString());const h=new CustomEvent("starwind-switch:change",{detail:{checked:s,switchId:this.switchButton.id},bubbles:!0,cancelable:!0});this.switchButton.dispatchEvent(h)}handleKeyDown(t){this.switchButton.disabled||(t.key===" "||t.key==="Enter")&&(t.preventDefault(),this.handleStateChange())}}const n=new WeakMap,i=()=>{document.querySelectorAll('.starwind-switch button[role="switch"]').forEach(e=>{n.has(e)||n.set(e,new c(e))})};i();document.addEventListener("astro:after-swap",i);
